import React, { useState } from 'react';
import axios from 'axios';

const CreateShortUrl = () => {
  const [url, setUrl] = useState('');
  const [shortcode, setShortcode] = useState('');
  const [validity, setValidity] = useState(30);
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');

  const handleCreate = async () => {
    try {
      setError('');
      const response = await axios.post('http://localhost:5000//api/shorturls', {
        url,
        shortcode: shortcode.trim() || undefined,
        validity: Number(validity),
      });
      setResult(response.data);
    } catch (err) {
      setError(err.response?.data?.error || 'Server error');
    }
  };

  return (
    <div className="card">
      <h2>Create Short URL</h2>
      <input
        type="text"
        placeholder="Original URL"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
      />
      <input
        type="text"
        placeholder="Custom shortcode (optional)"
        value={shortcode}
        onChange={(e) => setShortcode(e.target.value)}
      />
      <input
        type="number"
        placeholder="Validity in minutes (default 30)"
        value={validity}
        onChange={(e) => setValidity(e.target.value)}
      />
      <button onClick={handleCreate}>Shorten</button>
      {result && (
        <div className="result">
          <p><strong>Short Link:</strong> <a href={result.shortLink} target="_blank" rel="noreferrer">{result.shortLink}</a></p>
          <p><strong>Expires At:</strong> {new Date(result.expiry).toLocaleString()}</p>
        </div>
      )}
      {error && <p className="error">{error}</p>}
    </div>
  );
};

export default CreateShortUrl;
