import React, { useState } from 'react';
import axios from 'axios';

const CreateShortUrl = () => {
  const [originalUrl, setOriginalUrl] = useState('');
  const [shortUrl, setShortUrl] = useState('');
  const [error, setError] = useState('');

  const handleCreate = async () => {
    if (!originalUrl.trim()) {
      setError('URL is required');
      return;
    }

    try {
      const response = await axios.post('/api/shorturls', {
        originalUrl: originalUrl.trim(),
      });

      setShortUrl(`http://localhost:5000/${response.data.shortcode}`);
      setError('');
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.error || 'Something went wrong');
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <h2>üîó URL Shortener</h2>
      <input
        type="text"
        placeholder="Enter original URL"
        value={originalUrl}
        onChange={(e) => setOriginalUrl(e.target.value)}
        style={{ padding: '10px', width: '300px', marginRight: '10px' }}
      />
      <button onClick={handleCreate} style={{ padding: '10px' }}>
        Shorten
      </button>

      {error && <p style={{ color: 'red' }}>‚ùå {error}</p>}

      {shortUrl && (
        <p>
          ‚úÖ Short URL: <a href={shortUrl}>{shortUrl}</a>
        </p>
      )}
    </div>
  );
};

export default CreateShortUrl;
